package cn.algorithm.leetcode.weekly._198;

/**
 * @author hongzhou.wei
 * @date 2020/7/12
 */
public class P5461_NumberOfSubstringsWithOnly1s {
    public static void main(String[] args) {
//        String s = "";
        String s = "11100010101011110000011111";
        System.out.println(numSub(s));
        System.out.println(numSub1(s));
//        System.out.println(s.length());
    }

    /**
     * 1
     *
     * @param s
     * @return
     */
    static public int numSub(String s) {
        final long MOD = (int) (Math.pow(10, 9) + 7);
        String sub[] = s.split("0");
        long count = 0;
        for (int i = 0; i < sub.length; i++) {
            if (!"".equals(sub[i])) {
                count %= MOD;
                count += f((long) sub[i].length()) % MOD;
                count %= MOD;
            }
        }
        return (int) count;
    }

    static long f(long n) {
        long sum = 0;
        for (long i = 1; i <= n; i++) {
            sum += i;
        }
        return sum;
    }

    /**
     * 
     *
     * @param s
     * @return
     */
    static public int numSub1(String s) {
        final int MOD = 1000000007;
        long count1 = 0, sum = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                count1++;
            } else {
                sum += (count1 * (count1 + 1) / 2) % MOD;
                sum %= MOD;
                count1 = 0;
            }
        }
        sum += (count1 * (count1 + 1) / 2) % MOD;
        return (int) sum % MOD;
    }
}
